sqlite:     SELECT "from" AS col_name, "table" AS tab_ref, "to" AS col_ref, 'FK_' || seq AS name FROM pragma_foreign_key_list('{tab_name}');
duckdb:     SELECT kcu.column_name AS col_name, ccu.table_name AS tab_ref, ccu.column_name AS col_ref, tc.constraint_name AS name FROM information_schema.table_constraints tc JOIN information_schema.key_column_usage kcu ON tc.constraint_name = kcu.constraint_name JOIN information_schema.constraint_column_usage ccu ON ccu.constraint_name = tc.constraint_name WHERE tc.table_name = '{tab_name}' AND tc.constraint_type = 'FOREIGN KEY';
postgresql: SELECT kcu.column_name as col_name, ccu.table_name as tab_ref, ccu.column_name as col_ref, tc.constraint_name as name FROM information_schema.table_constraints tc JOIN information_schema.key_column_usage kcu ON tc.constraint_name = kcu.constraint_name JOIN information_schema.constraint_column_usage ccu ON ccu.constraint_name = tc.constraint_name WHERE tc.table_name = '{tab_name}' AND tc.constraint_type = 'FOREIGN KEY';
mysql:      SELECT COLUMN_NAME as col_name, REFERENCED_TABLE_NAME as tab_ref, REFERENCED_COLUMN_NAME as col_ref, CONSTRAINT_NAME as name FROM information_schema.KEY_COLUMN_USAGE WHERE TABLE_NAME = '{tab_name}' AND REFERENCED_TABLE_NAME IS NOT NULL;
mssql:      SELECT kcu.COLUMN_NAME as col_name, kcu.REFERENCED_TABLE_NAME as tab_ref, kcu.REFERENCED_COLUMN_NAME as col_ref, kcu.CONSTRAINT_NAME as name FROM INFORMATION_SCHEMA.KEY_COLUMN_USAGE kcu WHERE kcu.TABLE_NAME = '{tab_name}' AND kcu.REFERENCED_TABLE_NAME IS NOT NULL;